mapper.select:
SELECT stu_id, judge_id, ac_year, type, eval_status, score, eval_time, remark
FROM eval_records
WHERE type = #{type}, judge_id = #{judgeId}, ac_year = #{acYear}

mapper.update:
UPDATE eval_records
SET stu_card_id = #{stuCardId}, judge_card_id = #{judgeCardId}, ac_year = #{acYear},
    mat_type = #{matType}, eval_status = #{evalStatus}, score = #{score},
    eval_time = #{evalTime}, remark = #{remark}
WHERE stu_id = #{stuId} AND judge_id = #{judgeId}
